__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset entry: notify and ensure sofa outlet on",
    "parent": 0,
    "comment": "When front door light is on and sofa receives a fast-on at sunset, notify mobile and turn on outlet",
    "if": [
      { "(": 1 },
        { "==": { "device": "2A 66 1C 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "and": 1 },
        { "==": { "device": "2A 68 E0 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "n001_udmobile", "command": "GV10", "parameters": [ { "name": "Group", "value": 0, "uom": "25", "precision": 0 }, { "name": "Sound", "value": 2, "uom": "25", "precision": 0 } ] },
      { "device": "2A 68 E0 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Combine COS for FrontDoorExtEntryLight status==On and COC for LivingRoomSofaOutlet DFON in a group, limited to sunset. Notify via UD Mobile (GV10 using Sound=SMS Notification) and ensure sofa outlet is ON."
  }
}
__END_NUCORE_ROUTINE__
