__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Relay Q Press -> F9 Pressed",
    "parent": 0,
    "comment": "When IR Button Q is pressed, send Pressed to IR Button F9",
    "if": [
      { "==": { "device": "n009_irbutton_114", "control": "Pressed" } }
    ],
    "then": [
      { "device": "n009_irbutton_142", "command": "Pressed", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (== with control 'Pressed') on n009_irbutton_114 to detect a physical press of button Q and send the Pressed command to n009_irbutton_142 to relay the action."
  }
}
__END_NUCORE_ROUTINE__
