__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning door press -> warm tub lights",
    "parent": 0,
    "comment": "When KP.1 is manually turned on during weekday morning window, set Master Bath Tub warm and bright.",
    "if": [
      { "(": 1 },
        { "==": { "device": "51 75 63 1", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "n001_8801095facbb0b", "command": "SET_CTBR", "parameters": [ { "name": "Color Temperature", "value": 3000, "uom": "26", "precision": 1 }, { "name": "Transition Time", "value": 2000, "uom": "42", "precision": 1 }, { "name": "Brightness", "value": 180, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Separate routine triggers on a COC (DFON) for KP.1 limited to weekday 06:00-09:00. Action uses SET_CTBR on Master Bath Tub with explicit K, D, BR parameters and proper UOMs/precisions from DEVICE STRUCTURE. 'else' empty because this routine only covers the morning case."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Night door press -> dim warm tub lights",
    "parent": 0,
    "comment": "When KP.1 is physically pressed during night window, set Master Bath Tub to low warm level.",
    "if": [
      { "(": 1 },
        { "==": { "device": "51 75 63 1", "control": "DFON" } },
        { "and": 1 },
        { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "n001_8801095facbb0b", "command": "SET_CTBR", "parameters": [ { "name": "Color Temperature", "value": 2700, "uom": "26", "precision": 1 }, { "name": "Transition Time", "value": 1000, "uom": "42", "precision": 1 }, { "name": "Brightness", "value": 30, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Separate routine for door DFON during a night period (cross-midnight). Uses the device's SET_CTBR command with K, D, BR parameters defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
