__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn off landscaping lights when forecast is rain in evening",
    "parent": 0,
    "comment": "Turn both Med Palm Spot Back Left and Right off when forecast reports Rain during 18:00-22:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "n002_f521759638640", "status": "GV9", "value": 6, "uom": "25" } },
        { "and": 1 },
        { "from": { "time": "18:00", "to": { "time": "22:00", "day": 0 } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "n020_88010d2503c20b", "command": "DOF" },
      { "device": "n020_88010e1003500b", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Use a COS on the forecast weather (GV9 == 6 for Rain) combined with a schedule window from 18:00 to 22:00. When both conditions are true, send DOF (Off) to each landscaping light using their device IDs. No parameters required for DOF."
  }
}
__END_NUCORE_ROUTINE__
