__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday assoc ON -> All On",
    "parent": 0,
    "comment": "When assoc device is pressed ON on weekdays 08:00-18:00, send ALLON to Switch All",
    "if": [
      { "==": { "device": "ZY003_252N002", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "ZY003_183", "command": "ALLON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (button press DON) from ZY003_252N002 combined with a weekday time window to trigger ALLON on ZY003_183. This keeps the action limited to business hours and uses only accepted commands for the target device."
  }
}
__END_NUCORE_ROUTINE__
