__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "BRSWm fast-on before sunset -> LRSWs ramp and on",
    "parent": 0,
    "comment": "When the physical fast-on (DFON) is used on the BRSWm shortly before sunset, prepare LRSWs with a quick ramp and set to 80%",
    "if": [
      { "==": { "device": "12 40 40 1", "control": "DFON" } },
      { "and": 1 },
      { "at": { "sunset": -600 } }
    ],
    "then": [
      { "device": "12 40 77 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "12 40 77 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "User requested a COC (DFON on BRSWm) limited to the 10 minutes before sunset. Set LRSWs ramp rate to the 1.0 minute enum (value 12, uom 25) then send an On command at 80% (uom 51)."
  }
}
__END_NUCORE_ROUTINE__
