__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Cooktop Fan when family room switch fast-on on weekday mornings",
    "parent": 0,
    "comment": "Triggered by a fast-on (COC) on either family room switch during weekday morning window",
    "if": [
      { "(": 1 },
        { "==": { "device": "38 F7 B1 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "38 F7 B1 2", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "37 48 5 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a grouped COC trigger (DFON) for either Family Room top or bottom and restrict to weekdays 07:00-09:00 so the Cooktop Fan only turns on when a fast-on control occurs during morning hours. This avoids activating at other times and uses the devices' DFON control as provided in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
