__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Sync LR Side 2.2 when 2.1 fast-on in evening",
    "parent": 0,
    "comment": "On physical DFON of 4D 43 CA 1 between 18:00-23:00 daily, turn on sibling and set backlight to 70%",
    "if": [
      { "==": { "device": "4D 43 CA 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "4D 43 CA 2", "command": "DON" },
      { "device": "4D 43 CA 2", "command": "BL", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from device 4D 43 CA 1 so the routine triggers on a physical control. Restrict to evening hours with a weekly time window. Then turn on the paired device and set its backlight to a defined percentage using the BL command and its parameter range."
  }
}
__END_NUCORE_ROUTINE__
