__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "On manual garage start: set hub to Garage and start music",
    "parent": 0,
    "comment": "Trigger on physical Garage start (COC) — set hub activity and start music",
    "if": [
      { "==": { "device": "n010_a37718611", "control": "DON" } }
    ],
    "then": [
      { "device": "n010_h51415fc31d16b", "command": "SET_ACTIVITY", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] },
      { "device": "n010_a37718613", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Detects a manual (COC) start on the Garage (control DON). When the user physically starts the garage, the routine sets the Elite Hub activity to 'Garage' (enum value 1 using SET_ACTIVITY) and starts the Music - Family Room (DON). No else actions are required."
  }
}
__END_NUCORE_ROUTINE__
