__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset - FR1-CAB to 70%",
    "parent": 0,
    "comment": "Set FR1 at sunset",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      {
        "device":"3A 21 6C 1",
        "command":"DON",
        "parameters":[{ "name":"n/a","value":70,"uom":"51","precision":0 }]
      }
    ],
    "else": [],
    "reasoning": "Scheduled action: at local sunset set FR1-CAB BACK LTS to 70% using its DON command which accepts a 0-100% parameter (uom 51)."
  }
}
__END_NUCORE_ROUTINE____BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "SPARE1 DFON -> FR1 full",
    "parent": 0,
    "comment": "Physical fast-on on SPARE1 sets FR1 to 100%",
    "if": [
      { "==": { "device":"3A 1B D5 1","control":"DFON" } }
    ],
    "then": [
      {
        "device":"3A 21 6C 1",
        "command":"DON",
        "parameters":[{ "name":"n/a","value":100,"uom":"51","precision":0 }]
      }
    ],
    "else": [],
    "reasoning": "COC trigger: when SPARE1-LAMP MOD-02 is physically fast-on (DFON), immediately set FR1-CAB BACK LTS to 100% using its DON command."
  }
}
__END_NUCORE_ROUTINE__
