__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night tamper -> enable alert param 5",
    "parent": 0,
    "comment": "When tamper trips overnight, enable alert parameter and persist.",
    "if": [
      { "==": { "device": "ZY201_157", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "ZY201_1",
        "command": "CONFIG",
        "parameters": [
          { "name": "Parameter Number", "value": 5, "precision": 0 },
          { "name": "Parameter Value", "value": 1, "uom": "107", "precision": 0 }
        ]
      },
      {
        "device": "ZY201_1",
        "command": "WDU",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "A COS on the tamper alarm (ST -> On) during the overnight window should enable parameter 5 to raise alert behavior; WDU persists the change.">
  }
}
__END_NUCORE_ROUTINE__
