__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning Button1 fast-press -> Query Responder",
    "parent": 0,
    "comment": "Trigger responder query when Button 1 is fast-pressed during weekday mornings",
    "if": [
      { "==": { "device": "ZY143_201N001", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY143_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC equality for Button 1 DFON as the physical fast-press trigger, combined with a weekly schedule for weekdays 07:00-09:00. The only available actionable command in the DEVICE STRUCTURE is QUERY, so the routine sends QUERY to the Scene Responder when both conditions are met."
  }
}
__END_NUCORE_ROUTINE__
