__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion turns on GAR2 work lights",
    "parent": 0,
    "comment": "Turn on GAR2-WORK LTS when motion detected at night",
    "if": [
      { "==": { "device": "ZY188_155", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY058_1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Trigger on motion sensor state equal to On (COS) and restrict to nightly window 22:00-06:00; action sends the existing DON command to the GAR2-WORK LTS device to turn lights on."
  }
}
__END_NUCORE_ROUTINE__
