__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Nighttime garage ON sets sensor parameter and writes",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY104_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY104_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY172_1", "command": "CONFIG", "parameters": [ { "name": "NUM", "value": 5 }, { "name": "VAL", "value": 1, "uom": "107", "precision": 0 } ] },
      { "device": "ZY172_1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a grouped COC check for either garage module being physically turned ON (DON) and restrict to the overnight schedule from 22:00 to 06:00. On trigger, send CONFIG to set parameter 5 to 1 using the defined 1-byte unsigned UOM (107) and then WDU to persist changes. This avoids acting at other times and ensures manual ON causes the update."
  }
}
__END_NUCORE_ROUTINE__
