__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday physical AllOn/AllOff sets Options to Ignore None",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
      { "==": { "device": "ZY002_183", "control": "ALLON" } },
      { "or": 1 },
      { "==": { "device": "ZY002_183", "control": "ALLOFF" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY002_183", "command": "ALLSET", "parameters": [ { "name": "n/a", "value": 255, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When the Switch All is physically controlled during weekday business hours, set its Options enum to 255 (Ignore None) so group commands are propagated normally."
  }
}
__END_NUCORE_ROUTINE__
