__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Double-tap DFON at sunset — fast ramp and set to 80%",
    "parent": 0,
    "comment": "When user double-taps DFON on Deck Multi Lights A after sunset, set fastest ramp and turn on to 80%",
    "if": [
      { "==": { "device": "4E B2 1B 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "4E B2 1B 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 28, "uom": "25", "precision": 0 } ] },
      { "device": "4E B2 1B 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Double-tap DFON is a change-of-control intent; at sunset user likely wants lights on quickly. Set RR to index 28 (0.5s) then send DON=80% to the Deck Multi Lights A which accepts these commands."
  }
}
__END_NUCORE_ROUTINE__
