__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night alarm: beep Clear and poll others",
    "parent": 0,
    "comment": "When Clear detector alarms (COS to On) or is fast-activated (COC DFON) during night hours, beep it and poll the other detectors; otherwise poll all detectors.",
    "if": [
      { "(": 1 },
        { "==": { "device":"26 7A A 5", "status":"ST", "value":100, "uom":"51", "precision":0 } },
        { "or": 1 },
        { "==": { "device":"26 7A A 5", "control":"DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00", "to": { "time":"06:00", "day":1 } } }
    ],
    "then": [
      { "device":"26 7A A 5", "command":"BEEP", "parameters": [] },
      { "device":"26 7A A 6", "command":"QUERY", "parameters": [] },
      { "device":"26 7A A 7", "command":"QUERY", "parameters": [] }
    ],
    "else": [
      { "device":"26 7A A 5", "command":"QUERY", "parameters": [] },
      { "device":"26 7A A 6", "command":"QUERY", "parameters": [] },
      { "device":"26 7A A 7", "command":"QUERY", "parameters": [] }
    ],
    "reasoning": "Detect COS (Clear status -> On) or COC (Clear DFON) limited to overnight window; on that event beep the alarming detector and query the other detectors to obtain current states. Otherwise perform a status poll of all detectors."
  }
}
__END_NUCORE_ROUTINE__
