__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night garage open alert",
    "parent": 0,
    "comment": "Beep both garage devices when main opens overnight and is not responding",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"2B 5 C0 1","status":"ST","value":100,"uom":"51","precision":0 } },
      { "and": 1 },
      { "==": { "device":"2B 5 C0 1","status":"ERR","value":1,"uom":"25","precision":0 } }
    ],
    "then": [
      { "device":"2B 5 C0 1","command":"BEEP","parameters":[] },
      { "device":"2B 5 C0 3","command":"BEEP","parameters":[] }
    ],
    "else": [],
    "reasoning": "Overnight window (22:00-06:00) plus a COS that the main garage status is On (100) and Responding is False (1) should trigger audible alerts on both the main and low devices so occupants notice the unresponsive/open door. Uses BEEP (accepted command) on both devices." 
  }
}
__END_NUCORE_ROUTINE__
