__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Sunset: restore spare 2 backlight when not responding",
    "parent": 0,
    "comment": "If spare 2 is not responding at sunset boost backlight and save; otherwise set low backlight.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "5 35 42 1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "5 35 42 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "5 35 42 1", "command": "WDU", "parameters": [] }
    ],
    "else": [
      { "device": "5 35 42 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] },
      { "device": "5 35 42 1", "command": "WDU", "parameters": [] }
    ],
    "reasoning": "Run at sunset to check spare 2 Responding property; if False (1) increase backlight to 80 and persist with WDU; otherwise set a low backlight of 20 and persist. Uses only commands and properties defined for device 5 35 42 1."
  }
}
__END_NUCORE_ROUTINE__
