__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Christmas Tree when Palm Lights send On",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY027_252N002", "control": "DON" } }
    ],
    "then": [
      { "device": "ZY022_002_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Palm Lights Dual sends a physical On (COC), we immediately turn on the Christmas Tree (device ZY022_002_1)."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "At sunset set Master Closet Disco All On",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "ZY009_183", "command": "ALLON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "At local sunset, enable All On on the ZY 009 Switch All to illuminate the Master Closet Disco group."
  }
}
__END_NUCORE_ROUTINE__
