__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "SmokeMachine_On_Turn_Chandelier_AllOff",
    "parent": 0,
    "comment": "When ZY017_252N002 sends DON, turn ZY015_183 All Off",
    "if": [
      { "==": { "device": "ZY017_252N002", "control": "DON" } }
    ],
    "then": [
      { "device": "ZY015_183", "command": "ALLOFF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on the smoke machine's physical ON event (DON). When it occurs, send the ALLOFF command to the dining room chandelier (ZY015_183) which accepts ALLOFF. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
