__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Master Davenport: Fast-on sets warm bright",
    "parent": 0,
    "comment": "Press fast on to set 3000K and brightness 200 over 2s",
    "if": [
      { "==": { "device": "n001_88010e4b097c0b", "control": "DFON" } }
    ],
    "then": [
      { "device": "n001_88010e4b097c0b", "command": "SET_CTBR", "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
      ] }
    ],
    "else": [],
    "reasoning": "Use the DFON (fast on) COC from Master Davenport to quickly set a comfortable warm color temperature and bright level with a 2s transition."
  }
}
__END_NUCORE_ROUTINE__
