__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night closet open: beep low-battery & poll heartbeat",
    "parent": 0,
    "comment": "When Closet-C opens overnight, alert low-battery child and query heartbeat",
    "if": [
      { "(": 1 },
        { "==": { "device": "55 AE 2C 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "55 AE 2C 3", "command": "BEEP" },
      { "device": "55 90 9C 4", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "This routine triggers on a COS for Closet-C (Status ST -> On = 100) during the overnight window (22:00-06:00 spanning midnight). It beeps the Closet-C low-battery child sensor to draw attention and queries the Closet-D heartbeat to refresh its status for diagnostics."
  }
}
__END_NUCORE_ROUTINE__
