__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday double‑tap -> warm white 80%",
    "parent": 0,
    "comment": "Double‑tap (Fast On) on weekdays sets warm white 3000K and 80% brightness with 5s transition",
    "if": [
      { "==": { "device": "n002_d073d526f117", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "n002_d073d526f117", "command": "DON" },
      { "device": "n002_d073d526f117", "command": "RR", "parameters": [ { "name": "n/a", "value": 5000, "uom": "42", "precision": 1 } ] },
      { "device": "n002_d073d526f117", "command": "CLITEMP", "parameters": [ { "name": "n/a", "value": 3000, "uom": "26", "precision": 1 } ] },
      { "device": "n002_d073d526f117", "command": "SETB", "parameters": [ { "name": "n/a", "value": 52428, "uom": "56", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Use COC (DFON) + weekly schedule so a human fast‑on action during weekday work hours sets the desired color temperature and brightness. Turn on first, set transition time then color temp and brightness. Brightness 80% mapped to 0–65535 scale (approx. 52428)."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Single‑tap undercabinet -> off",
    "parent": 0,
    "comment": "A normal ON control (DON) from the controller will turn the light off to act as a single‑tap off shortcut",
    "if": [
      { "==": { "device": "n002_d073d526f117", "control": "DON" } }
    ],
    "then": [
      { "device": "n002_d073d526f117", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Map single ON control (COC DON) to an off action per the user's requested shortcut. This uses the device's accepted DON/DOF commands only." 
  }
}
__END_NUCORE_ROUTINE__
