__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on color lights at sunset",
    "parent": 0,
    "comment": "Activate both color lights at local sunset",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "ZB53281_011_106", "command": "DON", "parameters": [] },
      { "device": "ZB53281_011_108", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User requested both color-capable devices come on at sunset. Use a sunset schedule trigger and issue ON commands to each device. No conditional checks on current state to keep action deterministic at sunset."
  }
}
__END_NUCORE_ROUTINE__
