__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday SC-ON -> Charger On",
    "parent": 0,
    "comment": "Turn charger on when SC Basic Control is pressed ON during weekday business hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY171_251", "control": "DON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZM00004_001_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC trigger: user pressing SC Basic Control ON (ZY171_251 sends DON). Restrict to weekdays 09:00-17:00. Action uses charger accept command DON to turn it on. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
