__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening manual front-wall ON -> turn on Christmas lights",
    "parent": 0,
    "comment": "When front wall lights are manually turned on between 18:00 and 23:00, enable Christmas lights",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY003_1", "control": "DON" } },
        { "and": 1 },
        { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "18:00", "to": { "time": "23:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY004_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (control DON) from the front-wall device ZY003_1 combined with a daily evening time window to ensure manual local control during evening hours also powers the Christmas lights ZY004_1."
  }
}
__END_NUCORE_ROUTINE__
