__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weeknight Button1 Fast On polls",
    "parent": 0,
    "comment": "When Button 1 DFON during weekdays 18:00-22:00 query controller and Button2",
    "if": [
      { "==": { "device": "ZY052_201N001", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "ZY052_252N003", "command": "QUERY", "parameters": [] },
      { "device": "ZY052_201N002", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on a hardware Fast On (DFON) from Button1 during weekday evenings; actions issue QUERY to refresh state of scene controller and Button2."
  }
}
__END_NUCORE_ROUTINE__
