__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Night motion turns living room all on",
    "parent": 0,
    "comment": "Turn all living room switches on when motion is detected during night hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY189_155", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY189_155", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY003_183", "command": "ALLON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "During the 22:00-06:00 night window, either a motion event (DON) or the motion sensor status becoming On indicates occupancy; turning the Living RM 'All' switch ALLON improves safety and visibility." 
  }
}
__END_NUCORE_ROUTINE__
