__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Table Fast-On routes to Bookcase or Backyard at 60%",
    "parent": 0,
    "comment": "DFON on Table chooses Bookcase when responding, otherwise Backyard",
    "if": [
      { "(": 1 },
        { "==": { "device": "9 4F 19 3", "control": "DFON" } },
        { "and": 1 },
        { "==": { "device": "9 4F 19 4", "status": "ERR", "value": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "9 4F 19 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "9 4F 19 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "Trigger on the physical DFON (COC) from Table Light. Use the Bookcase Responding property (ERR==0) to decide target: if Bookcase responds, set its backlight to 60%; otherwise set Backyard backlight to 60%."
  }
}
__END_NUCORE_ROUTINE__
