__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 100,
    "name": "Double fast-off on KeypadLinc -> backlight off + fade down Jennifer",
    "parent": 0,
    "comment": "When KeypadLinc receives a double Fast Off COC, turn its backlight off and command Jennifer Can Light to fade down.",
    "if": [
      { "==": { "device": "1C 8B 2D 1", "control": "DFOF", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } }
    ],
    "then": [
      { "device": "1C 8B 2D 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "25", "precision": 0 } ] },
      { "device": "3D 86 9D 1", "command": "FDDOWN", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFOF with tapCount=2) from the KeypadLinc to represent a user double-press regardless of state. Then set the KeypadLinc backlight to 0 (Backlight command) and instruct the Jennifer Can Light to Fade Down (FDDOWN).">
  }
}
__END_NUCORE_ROUTINE__
