__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday mailbox heartbeat query",
    "parent": 0,
    "comment": "Query mailbox when it reports On during business hours",
    "if": [
      { "==": { "device":"4E 3A 2A 4","status":"ST","value":100,"uom":"51","precision":0 } },
      { "and": 1 },
      { "weekly": { "days":"mon,tue,wed,thu,fri","from":{ "time":"09:00","to":{ "time":"17:00" } } } }
    ],
    "then": [
      { "device":"4E 3A 2A 4","command":"QUERY","parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Mailbox-Heartbeat reports ST == On (100) during weekday business hours, automatically query it to confirm status and freshness of the heartbeat."
  }
}
__END_NUCORE_ROUTINE__
