__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night outlet ON - poll both outlets",
    "parent": 0,
    "comment": "When ZY012_252N002 is turned ON during night hours, query both outlets",
    "if": [
      { "==": { "device": "ZY012_252N002", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY012_252N002", "command": "QUERY", "parameters": [] },
      { "device": "ZY012_252N003", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the first outlet receives a physical ON control at night, immediately poll both outlet devices so the system has current status for automation and logging. Actions use QUERY, the only accepted command on these devices."
  }
}
__END_NUCORE_ROUTINE__
