__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: KP 1.H controls Patio Can Lights",
    "parent": 0,
    "comment": "At sunset, set can lights to 60% when KP 1.H is on; otherwise turn them off.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "51 71 A2 8", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "52 5A 7C 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "52 5A 7C 1", "command": "DOF" }
    ],
    "reasoning": "Run at sunset; check KP 1.H status as a COS condition (ST == On). If true, set Patio Can Lights On Level to 60% using OL (accepted by device). If not, ensure the group is turned off with DOF."
  }
}
__END_NUCORE_ROUTINE__
