__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Daytime: fan on when upstairs hot or closet occupied",
    "parent": 0,
    "comment": "Turn on downstairs fan when upstairs is hot or network closet occupied during daytime; otherwise set fan to auto.",
    "if": [
      { "(": 1 },
        { ">": { "device": "n002_t521782496039", "status": "ST", "value": 780, "uom": "17", "precision": 1 } },
        { "or": 1 },
        { "==": { "device": "n002_rs_8lln", "status": "GV1", "value": 1, "uom": "25", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "06:00", "to": { "time": "22:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n002_t521759638640", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 6, "uom": "67", "precision": 0 } ] },
      { "device": "n002_t521759638640", "command": "CLIFS", "parameters": [ { "name": "n/a", "value": 1, "uom": "68", "precision": 0 } ] }
    ],
    "else": [
      { "device": "n002_t521759638640", "command": "CLIFS", "parameters": [ { "name": "n/a", "value": 0, "uom": "68", "precision": 0 } ] }
    ],
    "reasoning": "If upstairs temp >78°F (ST value 780 precision 1) or network closet occupancy becomes Yes (GV1=1) during 06:00-22:00, set downstairs Mode to Fan Only (CLIMD=6) and Fan Mode On (CLIFS=1). Otherwise set Fan Mode to Auto (CLIFS=0)."
  }
}
__END_NUCORE_ROUTINE__
