__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening fast-on: warm bright or default",
    "parent": 0,
    "comment": "Tap DFON during evening gives warm bright scene; otherwise lower brightness.",
    "if": [
      { "==": { "device": "n001_88010cff470d0b", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "17:00", "to": { "time": "22:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n001_88010cff470d0b", "command": "SET_CTBR", "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 180, "uom": "100", "precision": 1 }
      ] }
    ],
    "else": [
      { "device": "n001_88010cff470d0b", "command": "SET_BRI", "parameters": [
          { "name": "n/a", "value": 100, "uom": "100", "precision": 1 }
      ] }
    ],
    "reasoning": "Use COC (DFON) combined with a time window to provide a warm, bright scene quickly; fallback lowers brightness when tap occurs outside the window."
  }
}
__END_NUCORE_ROUTINE__
