__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Porch double-press after sunset -> Set Soffit Study warm 3000K 80%",
    "parent": 0,
    "comment": "When Porch DFON double-press after sunset, set two study soffits to 3000K and high brightness with 2s transition",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n001_88010cff475f0b", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } }
    ],
    "then": [
      {
        "device": "n001_88010cff4a270b",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 204, "uom": "100", "precision": 1 }
        ]
      },
      {
        "device": "n001_88010d09aa470b",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 204, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use a COC (double-tap DFON with tapCount=2) gated by sunset. Apply SET_CTBR on each soffit to set 3000K, a 2000 ms transition and BR within allowed 1-254 range (204 ~ 80%)."
  }
}
__END_NUCORE_ROUTINE__
