__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Activate holiday lights on Front Yard DFON during evening",
    "parent": 0,
    "comment": "When the front yard fast-on is pressed in evening, enable other decorations and set backlight",
    "if": [
      { "==": { "device":"13 7 26 1", "control":"DFON" } },
      { "and": 1 },
      { "from": { "time":"17:00","to":{ "time":"23:00","day":0 } } }
    ],
    "then": [
      { "device":"1B 7A B0 1", "command":"DON", "parameters": [] },
      { "device":"1B 77 D5 1", "command":"DON", "parameters": [] },
      { "device":"13 7 26 1", "command":"BL", "parameters":[ { "name":"n/a","value":100,"uom":"51","precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from the Front Yard switch during evening hours to synchronously turn on the tree and entry wall lights and set the front yard backlight to full for visibility."
  }
}
__END_NUCORE_ROUTINE__
