__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night wet-sump alert: beep landing and query dry",
    "parent": 0,
    "comment": "On wet sensor COS during night, alert landing heartbeat and query dry sensor",
    "if": [
      { ">=": { "device": "55 7 F8 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "55 9 A4 4", "command": "BEEP", "parameters": [] },
      { "device": "55 7 F8 1", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when the kitchen wet sensor reports On (COS) during the night window 22:00-06:00; send a BEEP to the landing heartbeat device to alert and immediately QUERY the dry sensor to capture its state. Uses accepts-list commands only."
  }
}
__END_NUCORE_ROUTINE__
