__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Open shades and turn on bottom outlet when top fast-on pressed during work hours",
    "parent": 0,
    "comment": "Triggered by a COC (fast-on) on top outlet during weekday work hours",
    "if": [
      { "==": { "device": "4D 44 E2 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "n001_775288ab", "command": "DON" },
      { "device": "4D 44 E2 2", "command": "DON" }
    ],
    "else": [],
    "reasoning": "A physical Fast On (DFON) on the cabinet top during weekday work hours indicates user intent for lighting/visibility: open Shades Middle (DON) and turn on the bottom outlet (DON). No alternative action required.">
  }
}
__END_NUCORE_ROUTINE__
