__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening fast-press scene trigger",
    "parent": 0,
    "comment": "Fast press Button 1 during weekday evenings triggers responder query; outside time queries button1",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY143_201N001", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY143_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [
      { "device": "ZY143_201N001", "command": "QUERY", "parameters": [] }
    ],
    "reasoning": "Use DFON (fast on) from Button 1 as a COC and restrict to weekday evenings using a weekly schedule. When triggered in-window, query the responder to run the configured scene; if pressed outside the window, query the button to update/state-sync only."
  }
}
__END_NUCORE_ROUTINE__
