__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Deck sensor opens between sunset and +2h -> turn on relay 2",
    "parent": 0,
    "comment": "Activate relay #2 when deck sensor changes to On during the evening window",
    "if": [
      { "==": { "device": "ZY089_104", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "sunset": 7200 } } }
    ],
    "then": [
      { "device": "ZY056_002_1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Trigger on the binary sensor changing to On (COS). Constrain to the period from sunset to two hours after sunset using a 'from ... to' sunset offset. If true, send the ON (DON) command to relay ZY056_002_1 to illuminate the deck." 
  }
}
__END_NUCORE_ROUTINE__
