__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion turns on spare2",
    "parent": 0,
    "comment": "Turn on SPARE2 when motion detected at night",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"ZY172_155","status":"ST","value":100,"uom":"78","precision":0 } }
    ],
    "then": [
      { "device":"28 36 FB 1","command":"DFON","parameters":[] }
    ],
    "else": [],
    "reasoning": "Activate SPARE2 quickly when motion is detected during night hours using fast on. This routine triggers on the motion sensor state change to On (COS) and restricts operation to 22:00-06:00."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Night motion off turns off spare2",
    "parent": 0,
    "comment": "Turn off SPARE2 when motion stops at night",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"ZY172_155","status":"ST","value":0,"uom":"78","precision":0 } }
    ],
    "then": [
      { "device":"28 36 FB 1","command":"DOF","parameters":[] }
    ],
    "else": [],
    "reasoning": "Turn SPARE2 off when the motion sensor reports Off during the same night window. This ensures the device is only active while motion is present at night."
  }
}
__END_NUCORE_ROUTINE__
