__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Kitchen AllOn weekday morning set ignore none",
    "parent": 0,
    "comment": "Set Options to Ignore All On & All Off when ALLON is pressed during weekday morning",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY003_183", "control": "ALLON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY003_183", "command": "ALLSET", "parameters": [ { "name": "n/a", "value": 0, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "User wants a manual All On press (COC) to change the switch Options to enum 0, but only on weekdays between 07:00 and 09:00. Use a grouped COC + weekly schedule and call ALLSET with the parameter value 0 (uom 25)."
  }
}
__END_NUCORE_ROUTINE__
