__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: E Party ON -> Configure Picture Lights",
    "parent": 0,
    "comment": "Set picture lights backlight and power when E Party is turned on at sunset",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "1C 8C 48 5", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "1C 8C 48 7", "command": "BL", "parameters": [ { "name": "n/a", "value": 31, "uom": "25", "precision": 0 } ] },
      { "device": "1C 8C 48 7", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Trigger on the E Party device changing to On at sunset so the picture lights are made visible with a low backlight mode (31) and powered on. Using a schedule AND COS ensures this runs only when the On event occurs at evening time."
  }
}
__END_NUCORE_ROUTINE__
