__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night alert for Sliding Glass Door.3 open",
    "parent": 0,
    "comment": "Beep door and query leak sensor if door opens overnight",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "32 AF 85 3", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "32 AF 85 3", "command": "BEEP", "parameters": [] },
      { "device": "53 16 4 1", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "During overnight hours (22:00-06:00) an opening (Status == On) of Sliding Glass Door.3 likely indicates an alert condition; beep the door to draw attention and query the BU H2O sensor to get its current state for diagnostics."
  }
}
__END_NUCORE_ROUTINE__
