__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on backlight when motion after sunset",
    "parent": 0,
    "comment": "Set backlight to full when motion occurs shortly after sunset, otherwise turn off",
    "if": [
      { "at": { "sunset": 600 } },
      { "and": 1 },
      { ">=": { "device": "31 AB F6 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "9 4B 9C 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "9 4B 9C 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "Trigger is motion sensor state reporting On (ST >= 100) evaluated at 10 minutes after sunset. When both are true, set bad controlinc backlight to 100% using the device's Backlight (BL) accept command. Otherwise set backlight to 0% to keep it off." 
  }
}
__END_NUCORE_ROUTINE__
