__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "On-event: refresh master bedroom outlets",
    "parent": 0,
    "comment": "When either outlet is physically turned on (COC), query both outlets to update status",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY012_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY012_252N003", "control": "DON" } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY012_252N002", "command": "QUERY", "parameters": [] },
      { "device": "ZY012_252N003", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC 'DON' from either master bedroom outlet as the trigger. When a physical ON occurs, immediately query both outlets (they accept QUERY) so the system refreshes their reported states. No additional actions required on else."
  }
}
__END_NUCORE_ROUTINE__
