__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Party Lights at sunset",
    "parent": 0,
    "comment": "Enable the catwalk lights daily at sunset",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      {
        "device": "ZY032_1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 100, "uom": "78", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Schedule trigger at sunset to call the Party Lights Catwalk On command. The On command accepts a parameter enum (uom 78); use value 100 to ensure the lights are set to On. This runs daily at sunset."
  }
}
__END_NUCORE_ROUTINE__
