__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday KP.B double-tap: set Center Lights to 60%",
    "parent": 0,
    "comment": "When KP.B fast-on is triggered on weekday mornings, set room lights to 60%",
    "if": [
      { "(": 1 },
      { "==": { "device": "51 75 CA 2", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "51 75 CA 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "User requested a weekday-morning control path: use KP.B Fast On (DFON) as a COC trigger limited to mon–fri 06:00–09:00. Action sends DON to Master Bath Center Lights with 60% (uom 51, precision 2)."
  }
}
__END_NUCORE_ROUTINE__
